    public static List<Map<String,String>> jsonParse(String jsonStr) {
        List<Map<String,String>> returnListMap = new ArrayList<>();
        try {
            JSONObject jsonObject = new JSONObject(jsonStr);
            if (jsonObject.has("regeocodes")) {
                JSONArray regeocodes = jsonObject.getJSONArray("regeocodes");
                for (int i = 0;i < regeocodes.length();i++) {
                    JSONObject regeocode = regeocodes.getJSONObject(i);
                    Map<String,String> returnMap = new HashMap<>(4);
                    if (regeocode.has("aois")) {
                        JSONArray aois = regeocode.getJSONArray("aois");
                        if (aois != null && aois.length() > 0) {
                            JSONObject aoi = new JSONObject(String.valueOf(aois.get(0)));
                            String aoiType = String.valueOf(aoi.get("type"));
                            returnMap.put("aoiType",aoiType);
                        }
                    }
                    if (regeocode.has("pois")) {
                        JSONArray pois = regeocode.getJSONArray("pois");
                        if (pois != null && pois.length() > 0) {
                            JSONObject poi = new JSONObject(String.valueOf(pois.get(0)));
                            String poiType = String.valueOf(poi.get("type"));
                            returnMap.put("poiType",poiType);
                        }
                    }
                    if (regeocode.has("addressComponent")){
                        JSONObject needReturn = regeocode.getJSONObject("addressComponent");
                        StringBuffer stringBuffer = new StringBuffer();
                        String cityCode = needReturn.getString("citycode");
                        stringBuffer.append(cityCode+"|");
                        String province = needReturn.getString("province");
                        String city =  needReturn.getString("city");
                        if ("[]".equals(city)){
                            city = null;
                        }
                        if (city == null && province.contains("北京")
                                || province.contains("上海")
                                || province.contains("天津")
                                || province.contains("重庆")){
                            stringBuffer.append(province);
                        }else if (city == null){
                            stringBuffer.append( needReturn.getString("district"));
                        }else{
                            stringBuffer.append(city);
                        }
                        returnMap.put("cityCodeAndName",stringBuffer.toString());
                    }
                    returnListMap.add(returnMap);
                }
            }
        } catch (JSONException e) {
            System.out.println(e.getMessage());
        }
        return returnListMap;
    }

